SET NAMES utf8mb4;
SET FOREIGN_KEY_CHECKS = 0;

DROP TABLE IF EXISTS `loai`;
CREATE TABLE `loai`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `name` varchar(225) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 6 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;


INSERT INTO `loai` VALUES (3, 'Quần nam');
INSERT INTO `loai` VALUES (5, 'Quần áo nữs');


DROP TABLE IF EXISTS `post`;
CREATE TABLE `post`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `name` varchar(225) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `image` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `slug` varchar(225) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 7 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;


INSERT INTO `post` VALUES (1, 'Làm sao để có thể mặc thật ', '<p>Để c&oacute; thể mặc thật đẹp cho sinh vi&ecirc;n, bạn c&oacute; thể thực hiện một số bước sau</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>Để c&oacute; thể mặc thật đẹp cho sinh vi&ecirc;n, bạn c&oacute; thể thực hiện một số bước sau:</p>\r\n\r\n<ol>\r\n	<li>\r\n	<p>Tư vấn về phong c&aacute;ch mặc: H&atilde;y tư vấn v&agrave; cung cấp &yacute; kiến cho sinh vi&ecirc;n về phong c&aacute;ch mặc ph&ugrave; hợp với từng dịp v&agrave; ho&agrave;n cảnh. Điều n&agrave;y bao gồm việc chọn trang phục hợp thời trang, ph&ugrave; hợp với h&igrave;nh d&aacute;ng cơ thể v&agrave; tạo sự tự tin cho sinh vi&ecirc;n.</p>\r\n	</li>\r\n	<li>\r\n	<p>Chọn trang phục th&iacute;ch hợp: T&ugrave;y thuộc v&agrave;o từng dịp như dự tiệc, đi học, đi l&agrave;m, hay c&aacute;c hoạt động thể thao, bạn n&ecirc;n gi&uacute;p sinh vi&ecirc;n chọn trang phục ph&ugrave; hợp v&agrave; thoải m&aacute;i.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết hợp m&agrave;u sắc v&agrave; kiểu d&aacute;ng: H&atilde;y gi&uacute;p sinh vi&ecirc;n kết hợp m&agrave;u sắc v&agrave; kiểu d&aacute;ng trang phục một c&aacute;ch h&agrave;i h&ograve;a để tạo n&ecirc;n phong c&aacute;ch ri&ecirc;ng v&agrave; c&aacute; nh&acirc;n.</p>\r\n	</li>\r\n	<li>\r\n	<p>Chọn phụ kiện ph&ugrave; hợp: Phụ kiện như t&uacute;i x&aacute;ch, gi&agrave;y d&eacute;p, mũ, k&iacute;nh m&aacute;t,... c&oacute; thể l&agrave;m nổi bật trang phục v&agrave; tạo điểm nhấn cho phong c&aacute;ch mặc.</p>\r\n	</li>\r\n	<li>\r\n	<p>T&ocirc;n vinh n&eacute;t đẹp c&aacute; nh&acirc;n: Lu&ocirc;n đề cao n&eacute;t đẹp c&aacute; nh&acirc;n của sinh vi&ecirc;n v&agrave; tạo điều kiện để họ tỏa s&aacute;ng v&agrave; tự tin với phong c&aacute;ch mặc của ri&ecirc;ng m&igrave;nh.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đ&aacute;p ứng nhu cầu v&agrave; sở th&iacute;ch: Lắng nghe v&agrave; t&igrave;m hiểu về sở th&iacute;ch v&agrave; nhu cầu của sinh vi&ecirc;n để gi&uacute;p họ c&oacute; được trang phục v&agrave; phong c&aacute;ch mặc ưng &yacute;.</p>\r\n	</li>\r\n	<li>\r\n	<p>Khuyến kh&iacute;ch tự thể hiện: Kh&ocirc;ng n&ecirc;n &eacute;p buộc sinh vi&ecirc;n theo một phong c&aacute;ch cụ thể, m&agrave; h&atilde;y khuyến kh&iacute;ch họ tự thể hiện v&agrave; tạo n&ecirc;n phong c&aacute;ch ri&ecirc;ng của m&igrave;nh.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đồng h&agrave;nh v&agrave; hỗ trợ: Lu&ocirc;n sẵn l&ograve;ng đồng h&agrave;nh v&agrave; hỗ trợ sinh vi&ecirc;n trong việc lựa chọn trang phục v&agrave; phong c&aacute;ch mặc, gi&uacute;p họ tự tin v&agrave; hạnh ph&uacute;c với h&igrave;nh ảnh của m&igrave;nh.</p>\r\n	</li>\r\n</ol>\r\n', 'upload/w4.jpg', 'làm-sao-để-có-thể-mặc-thật-');
INSERT INTO `post` VALUES (2, 'bài viết về phóng cách thời trang của giới trẻ vn', '<p>b&agrave;i viết về ph&oacute;ng c&aacute;ch thời trang của giới trẻ vn</p>\r\n', '<p>Ph&oacute;ng c&aacute;ch thời trang của giới trẻ Việt Nam ng&agrave;y nay đang thể hiện sự đa dạng v&agrave; độc đ&aacute;o, phản &aacute;nh c&aacute; t&iacute;nh v&agrave; sở th&iacute;ch của từng c&aacute; nh&acirc;n. Dưới đ&acirc;y l&agrave; m&ocirc; tả về phong c&aacute;ch thời trang của giới trẻ Việt Nam:</p>\r\n\r\n<ol>\r\n	<li>\r\n	<p>T&iacute;nh s&aacute;ng tạo v&agrave; tự do thể hiện bản th&acirc;n: Giới trẻ Việt Nam thể hiện sự s&aacute;ng tạo v&agrave; tự do trong c&aacute;ch ăn mặc, từ việc kết hợp trang phục truyền thống với phụ kiện hiện đại, cho đến việc tạo n&ecirc;n c&aacute;c phong c&aacute;ch thời trang mới lạ v&agrave; c&aacute; nh&acirc;n.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết hợp giữa truyền thống v&agrave; hiện đại: Giới trẻ Việt Nam th&iacute;ch kết hợp giữa c&aacute;c yếu tố truyền thống v&agrave; hiện đại trong c&aacute;ch ăn mặc. V&iacute; dụ, họ c&oacute; thể mix &aacute;o d&agrave;i cổ điển với gi&agrave;y sneakers thể thao hoặc &aacute;o sơ mi truyền thống với quần jeans.</p>\r\n	</li>\r\n	<li>\r\n	<p>Ưu ti&ecirc;n sự thoải m&aacute;i v&agrave; thể hiện c&aacute; nh&acirc;n: Thay v&igrave; những trang phục phức tạp, giới trẻ Việt Nam th&iacute;ch ưu ti&ecirc;n c&aacute;c trang phục thoải m&aacute;i, ph&ugrave; hợp với hoạt động h&agrave;ng ng&agrave;y v&agrave; thể hiện c&aacute; nh&acirc;n.</p>\r\n	</li>\r\n	<li>\r\n	<p>Y&ecirc;u th&iacute;ch trang phục đường phố (Streetwear): Phong c&aacute;ch thời trang đường phố đang trở th&agrave;nh xu hướng phổ biến trong giới trẻ Việt Nam. C&aacute;c trang phục đường phố thường bao gồm quần jogger, &aacute;o hoodie, gi&agrave;y thể thao, v&agrave; c&aacute;c phụ kiện thời trang phong c&aacute;ch.</p>\r\n	</li>\r\n	<li>\r\n	<p>M&agrave;u sắc v&agrave; họa tiết đa dạng: Giới trẻ Việt Nam y&ecirc;u th&iacute;ch sự đa dạng trong m&agrave;u sắc v&agrave; họa tiết của trang phục. Họ thường chọn c&aacute;c m&agrave;u sắc tươi s&aacute;ng v&agrave; họa tiết phong c&aacute;ch để thể hiện c&aacute; t&iacute;nh v&agrave; sự vui vẻ.</p>\r\n	</li>\r\n	<li>\r\n	<p>Ảnh hưởng từ người nổi tiếng v&agrave; truyền th&ocirc;ng x&atilde; hội: Giới trẻ Việt Nam thường xem người nổi tiếng v&agrave; truyền th&ocirc;ng x&atilde; hội l&agrave; nguồn cảm hứng cho phong c&aacute;ch thời trang của họ. Họ cập nhật xu hướng thời trang qua c&aacute;c mạng x&atilde; hội v&agrave; ứng dụng di động.</p>\r\n	</li>\r\n	<li>\r\n	<p>Th&iacute;ch kh&aacute;m ph&aacute; v&agrave; thử nghiệm: Giới trẻ Việt Nam th&iacute;ch kh&aacute;m ph&aacute; v&agrave; thử nghiệm c&aacute;c phong c&aacute;ch thời trang mới, từ streetwear, vintage cho đến phong c&aacute;ch retro hoặc cổ điển.</p>\r\n	</li>\r\n</ol>\r\n\r\n<p>Tổng quan, phong c&aacute;ch thời trang của giới trẻ Việt Nam đang thể hiện sự đa dạng, tự do, v&agrave; s&aacute;ng tạo. Họ thể hiện c&aacute; t&iacute;nh v&agrave; ưa th&iacute;ch sự thoải m&aacute;i, kết hợp giữa truyền thống v&agrave; hiện đại, v&agrave; tạo n&ecirc;n những xu hướng thời trang mới lạ v&agrave; độc đ&aacute;o.</p>\r\n', 'upload/banner1.jpg', 'bài-viết-về-phóng-cách-thời-trang-của-giới-trẻ-vn');
INSERT INTO `post` VALUES (3, 'sinh viên đang thực hành tạo website bán hàng', '<p>sinh vi&ecirc;n đang thực h&agrave;nh tạo website b&aacute;n h&agrave;ng</p>\r\n', '<p>M&ocirc; tả: Ti&ecirc;u đề n&agrave;y hướng tới nh&oacute;m đối tượng l&agrave; sinh vi&ecirc;n đang tham gia v&agrave;o qu&aacute; tr&igrave;nh thực h&agrave;nh để tạo một website b&aacute;n h&agrave;ng. N&oacute; tỏ ra kh&aacute; hấp dẫn v&agrave; th&uacute; vị bởi sự kết hợp giữa hai yếu tố ch&iacute;nh: &quot;sinh vi&ecirc;n thực h&agrave;nh&quot; v&agrave; &quot;tạo website b&aacute;n h&agrave;ng&quot;.</p>\r\n\r\n<ol>\r\n	<li>\r\n	<p>&quot;Sinh vi&ecirc;n thực h&agrave;nh&quot;: Bắt đầu với &quot;Sinh vi&ecirc;n thực h&agrave;nh&quot;, ti&ecirc;u đề n&agrave;y thể hiện r&otilde; rằng đ&acirc;y l&agrave; cơ hội v&agrave; trải nghiệm d&agrave;nh cho nh&oacute;m sinh vi&ecirc;n. N&oacute; c&oacute; thể l&agrave; một phần của một kh&oacute;a học, dự &aacute;n học tập hoặc chương tr&igrave;nh đ&agrave;o tạo chuy&ecirc;n s&acirc;u.</p>\r\n	</li>\r\n	<li>\r\n	<p>&quot;Tạo Website B&aacute;n H&agrave;ng&quot;: Phần tiếp theo l&agrave; &quot;Tạo Website B&aacute;n H&agrave;ng&quot;, nhấn mạnh đến mục ti&ecirc;u cụ thể của sinh vi&ecirc;n trong qu&aacute; tr&igrave;nh thực h&agrave;nh. Điều n&agrave;y đưa ra một nhiệm vụ r&otilde; r&agrave;ng v&agrave; li&ecirc;n quan đến lĩnh vực kinh doanh v&agrave; c&ocirc;ng nghệ th&ocirc;ng tin.</p>\r\n	</li>\r\n	<li>\r\n	<p>&quot;Kh&aacute;m ph&aacute; khả năng s&aacute;ng tạo v&agrave; kinh doanh của bạn!&quot;: Cuối c&ugrave;ng, ti&ecirc;u đề đề cập đến gi&aacute; trị v&agrave; lợi &iacute;ch m&agrave; sinh vi&ecirc;n c&oacute; thể nhận được từ việc tham gia v&agrave;o qu&aacute; tr&igrave;nh thực h&agrave;nh n&agrave;y. N&oacute; th&uacute;c đẩy sinh vi&ecirc;n tập trung v&agrave;o việc kh&aacute;m ph&aacute; v&agrave; ph&aacute;t triển khả năng s&aacute;ng tạo v&agrave; kỹ năng kinh doanh của họ th&ocirc;ng qua việc x&acirc;y dựng một trang web b&aacute;n h&agrave;ng thực tế.</p>\r\n	</li>\r\n</ol>\r\n\r\n<p>Tổng cộng, ti&ecirc;u đề n&agrave;y kh&ocirc;ng chỉ m&ocirc; tả ch&iacute;nh x&aacute;c nội dung m&agrave; c&ograve;n tạo cảm hứng v&agrave; th&uacute;c đẩy sinh vi&ecirc;n tham gia t&iacute;ch cực v&agrave;o qu&aacute; tr&igrave;nh học tập v&agrave; thực h&agrave;nh.</p>\r\n', 'upload/anh23_2.jpg', 'sinh-viên-đang-thực-hành-tạo-website-bán-hàng');
INSERT INTO `post` VALUES (4, 'Làm sao để có thể mặc thật ', '<p>Để c&oacute; thể mặc thật đẹp cho sinh vi&ecirc;n, bạn c&oacute; thể thực hiện một số bước sau</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>Để c&oacute; thể mặc thật đẹp cho sinh vi&ecirc;n, bạn c&oacute; thể thực hiện một số bước sau:</p>\r\n\r\n<ol>\r\n	<li>\r\n	<p>Tư vấn về phong c&aacute;ch mặc: H&atilde;y tư vấn v&agrave; cung cấp &yacute; kiến cho sinh vi&ecirc;n về phong c&aacute;ch mặc ph&ugrave; hợp với từng dịp v&agrave; ho&agrave;n cảnh. Điều n&agrave;y bao gồm việc chọn trang phục hợp thời trang, ph&ugrave; hợp với h&igrave;nh d&aacute;ng cơ thể v&agrave; tạo sự tự tin cho sinh vi&ecirc;n.</p>\r\n	</li>\r\n	<li>\r\n	<p>Chọn trang phục th&iacute;ch hợp: T&ugrave;y thuộc v&agrave;o từng dịp như dự tiệc, đi học, đi l&agrave;m, hay c&aacute;c hoạt động thể thao, bạn n&ecirc;n gi&uacute;p sinh vi&ecirc;n chọn trang phục ph&ugrave; hợp v&agrave; thoải m&aacute;i.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết hợp m&agrave;u sắc v&agrave; kiểu d&aacute;ng: H&atilde;y gi&uacute;p sinh vi&ecirc;n kết hợp m&agrave;u sắc v&agrave; kiểu d&aacute;ng trang phục một c&aacute;ch h&agrave;i h&ograve;a để tạo n&ecirc;n phong c&aacute;ch ri&ecirc;ng v&agrave; c&aacute; nh&acirc;n.</p>\r\n	</li>\r\n	<li>\r\n	<p>Chọn phụ kiện ph&ugrave; hợp: Phụ kiện như t&uacute;i x&aacute;ch, gi&agrave;y d&eacute;p, mũ, k&iacute;nh m&aacute;t,... c&oacute; thể l&agrave;m nổi bật trang phục v&agrave; tạo điểm nhấn cho phong c&aacute;ch mặc.</p>\r\n	</li>\r\n	<li>\r\n	<p>T&ocirc;n vinh n&eacute;t đẹp c&aacute; nh&acirc;n: Lu&ocirc;n đề cao n&eacute;t đẹp c&aacute; nh&acirc;n của sinh vi&ecirc;n v&agrave; tạo điều kiện để họ tỏa s&aacute;ng v&agrave; tự tin với phong c&aacute;ch mặc của ri&ecirc;ng m&igrave;nh.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đ&aacute;p ứng nhu cầu v&agrave; sở th&iacute;ch: Lắng nghe v&agrave; t&igrave;m hiểu về sở th&iacute;ch v&agrave; nhu cầu của sinh vi&ecirc;n để gi&uacute;p họ c&oacute; được trang phục v&agrave; phong c&aacute;ch mặc ưng &yacute;.</p>\r\n	</li>\r\n	<li>\r\n	<p>Khuyến kh&iacute;ch tự thể hiện: Kh&ocirc;ng n&ecirc;n &eacute;p buộc sinh vi&ecirc;n theo một phong c&aacute;ch cụ thể, m&agrave; h&atilde;y khuyến kh&iacute;ch họ tự thể hiện v&agrave; tạo n&ecirc;n phong c&aacute;ch ri&ecirc;ng của m&igrave;nh.</p>\r\n	</li>\r\n	<li>\r\n	<p>Đồng h&agrave;nh v&agrave; hỗ trợ: Lu&ocirc;n sẵn l&ograve;ng đồng h&agrave;nh v&agrave; hỗ trợ sinh vi&ecirc;n trong việc lựa chọn trang phục v&agrave; phong c&aacute;ch mặc, gi&uacute;p họ tự tin v&agrave; hạnh ph&uacute;c với h&igrave;nh ảnh của m&igrave;nh.</p>\r\n	</li>\r\n</ol>\r\n', 'upload/w4.jpg', 'làm-sao-để-có-thể-mặc-thật-');
