DROP TABLE IF EXISTS `sanpham`;
CREATE TABLE `sanpham`  (
  `id` int NOT NULL AUTO_INCREMENT,
  `name` varchar(225) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `price` decimal(10, 0) NOT NULL,
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `image` varchar(225) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL,
  `id_loai` int NOT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  INDEX `id_loai`(`id_loai` ASC) USING BTREE,
  CONSTRAINT `id_loai` FOREIGN KEY (`id_loai`) REFERENCES `loai` (`id`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 12 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci ROW_FORMAT = Dynamic;


INSERT INTO `sanpham` VALUES (2, 'Quần đùi của nam', 129129012, '<p>Quần&nbsp;</p>\r\n', '<p>Quần đ&ugrave;i chưa biết</p>\r\n', 'upload/t3.jpg', 3);
INSERT INTO `sanpham` VALUES (4, 'Quần đùi nam', 200000, '<p>Quần đ&ugrave;i nam&nbsp;Quần đ&ugrave;i nam&nbsp;Quần đ&ugrave;i nam</p>\r\n', '<p>Ph&oacute;ng c&aacute;ch thời trang của giới trẻ Việt Nam ng&agrave;y nay đang thể hiện sự đa dạng v&agrave; độc đ&aacute;o, phản &aacute;nh c&aacute; t&iacute;nh v&agrave; sở th&iacute;ch của từng c&aacute; nh&acirc;n. Dưới đ&acirc;y l&agrave; m&ocirc; tả về phong c&aacute;ch thời trang của giới trẻ Việt Nam:</p>\r\n\r\n<ol>\r\n	<li>\r\n	<p>T&iacute;nh s&aacute;ng tạo v&agrave; tự do thể hiện bản th&acirc;n: Giới trẻ Việt Nam thể hiện sự s&aacute;ng tạo v&agrave; tự do trong c&aacute;ch ăn mặc, từ việc kết hợp trang phục truyền thống với phụ kiện hiện đại, cho đến việc tạo n&ecirc;n c&aacute;c phong c&aacute;ch thời trang mới lạ v&agrave; c&aacute; nh&acirc;n.</p>\r\n	</li>\r\n	<li>\r\n	<p>Kết hợp giữa truyền thống v&agrave; hiện đại: Giới trẻ Việt Nam th&iacute;ch kết hợp giữa c&aacute;c yếu tố truyền thống v&agrave; hiện đại trong c&aacute;ch ăn mặc. V&iacute; dụ, họ c&oacute; thể mix &aacute;o d&agrave;i cổ điển với gi&agrave;y sneakers thể thao hoặc &aacute;o sơ mi truyền thống với quần jeans.</p>\r\n	</li>\r\n	<li>\r\n	<p>Ưu ti&ecirc;n sự thoải m&aacute;i v&agrave; thể hiện c&aacute; nh&acirc;n: Thay v&igrave; những trang phục phức tạp, giới trẻ Việt Nam th&iacute;ch ưu ti&ecirc;n c&aacute;c trang phục thoải m&aacute;i, ph&ugrave; hợp với hoạt động h&agrave;ng ng&agrave;y v&agrave; thể hiện c&aacute; nh&acirc;n.</p>\r\n	</li>\r\n	<li>\r\n	<p>Y&ecirc;u th&iacute;ch trang phục đường phố (Streetwear): Phong c&aacute;ch thời trang đường phố đang trở th&agrave;nh xu hướng phổ biến trong giới trẻ Việt Nam. C&aacute;c trang phục đường phố thường bao gồm quần jogger, &aacute;o hoodie, gi&agrave;y thể thao, v&agrave; c&aacute;c phụ kiện thời trang phong c&aacute;ch.</p>\r\n	</li>\r\n	<li>\r\n	<p>M&agrave;u sắc v&agrave; họa tiết đa dạng: Giới trẻ Việt Nam y&ecirc;u th&iacute;ch sự đa dạng trong m&agrave;u sắc v&agrave; họa tiết của trang phục. Họ thường chọn c&aacute;c m&agrave;u sắc tươi s&aacute;ng v&agrave; họa tiết phong c&aacute;ch để thể hiện c&aacute; t&iacute;nh v&agrave; sự vui vẻ.</p>\r\n	</li>\r\n	<li>\r\n	<p>Ảnh hưởng từ người nổi tiếng v&agrave; truyền th&ocirc;ng x&atilde; hội: Giới trẻ Việt Nam thường xem người nổi tiếng v&agrave; truyền th&ocirc;ng x&atilde; hội l&agrave; nguồn cảm hứng cho phong c&aacute;ch thời trang của họ. Họ cập nhật xu hướng thời trang qua c&aacute;c mạng x&atilde; hội v&agrave; ứng dụng di động.</p>\r\n	</li>\r\n	<li>\r\n	<p>Th&iacute;ch kh&aacute;m ph&aacute; v&agrave; thử nghiệm: Giới trẻ Việt Nam th&iacute;ch kh&aacute;m ph&aacute; v&agrave; thử nghiệm c&aacute;c phong c&aacute;ch thời trang mới, từ streetwear, vintage cho đến phong c&aacute;ch retro hoặc cổ điển.</p>\r\n	</li>\r\n</ol>\r\n\r\n<p>Tổng quan, phong c&aacute;ch thời trang của giới trẻ Việt Nam đang thể hiện sự đa dạng, tự do, v&agrave; s&aacute;ng tạo. Họ thể hiện c&aacute; t&iacute;nh v&agrave; ưa th&iacute;ch sự thoải m&aacute;i, kết hợp giữa truyền thống v&agrave; hiện đại, v&agrave; tạo n&ecirc;n những xu hướng thời trang mới lạ v&agrave; độc đ&aacute;o.<img alt=\"\" src=\"data:image/jpeg;base64,\" style=\"height:121px; width:121px\" /></p>\r\n', 'upload/4d365804bf197b6c9892e87209fa9f6f.jpg', 3);
